INSERT INTO `baby_vaccinations` (`id`, `baby_id`, `date`, `name`, `description`) VALUES
(1, 6, '2013-02-20', 'акдс', 'трприрп'),
(2, 10, '2013-02-27', 'Инфанрикс', 'Последняя прививка из серии против коклюша, дифтерии и столбняка. Делали около 13.00. Перенесла без температуры. Вечером уже ходили гулять. Небольшое покраснение в области укола и припухлость'),
(4, 10, '2012-03-01', 'реакция Манту', 'IE 10/4 0.025 серия 92. Необходимо сделать БЦЖ-М в течении 2х недель'),
(5, 10, '2012-03-15', 'БЦЖ-М', 'Сделали первую прививку. Алисе не понравилось, что ее держали. Но это было необходимо, так как это подкожная прививка на руке. Перенесла хорошо. '),
(6, 37, '2013-03-05', 'Гепатит B', 'Моя первая вакцинация в ножку. Было больно, но я плакал не долго!'),
(7, 37, '2013-03-08', 'БЦЖ', 'Сделали в левую ручку. Я потом долго спал под лучиками яркого, весеннего солнышка'),
(8, 37, '2013-04-09', 'Гепатит B', 'Вторая вакцинация. Сделала мне ее в детской поликлинике № 3 старая строгая тетя, но мамочка и няняйка с татайкой меня быстро успокоили'),
(9, 37, '2013-06-04', 'АКДС', 'Очень болючая прививка. Молодая тетя-медсестра громко и ласково со мной разговаривала. Я ее слушал и почти забыл про укольчик'),
(10, 37, '2013-06-04', 'Полиомиелит', 'Вкололи во вторую ножку. Испачкали белый костюмчик кровью. Плакал долго и много. Я даже на маму обиделся. Она потом давала мне сироп Нурофен и таблеточки Супрастин, по совету педиатра. Температура не поднималась. ');
